const url="http://127.0.0.1:5500",user=JSON.parse(localStorage.getItem("user")),title=document.getElementById("titleInput"),description=document.getElementById("descriptionInput"),btn=document.getElementById("saveBtn"),titleAlert=document.getElementById("titleAlert"),descriptionAlert=document.getElementById("descriptionAlert"),postBug=()=>{fetch(`http://greenvelvet.alwaysdata.net/bugTracker/api/add/${user.token}/${user.id}`,{method:"post",body:JSON.stringify({title:title.value,description:description.value})}).then(res=>res.json()).then(response=>{"done"===response.result.status&&Swal.fire("Succès !","Le bug a bien été ajouté à la liste !","success").then(()=>{window.location.href=`${url}/pages/todo.html`})}).catch(error=>{console.log(error)})};btn.addEventListener("click",e=>{titleAlert.style.display="none",descriptionAlert.style.display="none",e.preventDefault(),""===title.value?titleAlert.style.display="block":""===description.value?descriptionAlert.style.display="block":postBug()});